SET NAMES UTF8;
DROP DATABASE IF EXISTS ts;
CREATE DATABASE ts CHARSET=UTF8;
USE ts;
/*创建用户列表*/
CREATE TABLE ts_user(
    uid INT PRIMARY KEY AUTO_INCREMENT,
    uname VARCHAR(32) UNIQUE,
    upwd VARCHAR(32),
    phone VARCHAR(32) ,
    money INT
);
/*创建首页图书表*/
CREATE TABLE ts_index(
    iid INT PRIMARY KEY AUTO_INCREMENT,
    title VARCHAR(128),         # 标题
    subtitle VARCHAR(1000),     # 副标题
    author  VARCHAR(128),       # 作者
    img VARCHAR(128),           # 图片地址 
    class VARCHAR(128),         # 类别
    href VARCHAR(128)           # 跳转地址
);
/*首页排行榜*/
CREATE TABLE ts_hot(
    hid INT PRIMARY KEY AUTO_INCREMENT,
    title VARCHAR(128),
    img VARCHAR(128),
    href VARCHAR(128)
);
/*首页轮播图表*/
CREATE TABLE ts_banner(
    bid INT PRIMARY KEY AUTO_INCREMENT,
    title VARCHAR(128),
    subtitle VARCHAR(1000),
    img VARCHAR(128),
    href VARCHAR(128)
);
CREATE TABLE ts_list(
	lid INT PRIMARY KEY AUTO_INCREMENT,
	author VARCHAR(128),
	title VARCHAR(128),
	img VARCHAR(128),
	href VARCHAR(128),
	subtitle VARCHAR(128)
);
CREATE TABLE ts_detail(
    tid INT PRIMARY KEY AUTO_INCREMENT,
    title VARCHAR(128),
    subtitle VARCHAR(1000),
    author VARCHAR(128),
    img VARCHAR(128),
    price INT,
    num INT
);
